import json
from groundscanner.groundscanner.map.segment import Segment


class Map:
    # array from type Segment [][] with name segments with heighth and width 19 and given type Segment  [[]]
    segments: [[]]

    def __init__(self):
        self.map = [[0 for _ in range(self.width)] for _ in range(self.height)]

    def loadMap(self, json_string: str):
        parsed_data = json.loads(json_string)
        self.segments = [
        [Segment(**segment) for segment in row]
        for row in parsed_data["arena"]["segments"]
    ]
        
