from groundscanner.groundscanner.map.map import Map
import rclpy
from rclpy.node import Node

from std_msgs.msg import String


class ScannerNode(Node):
    def __init__(self):
        super().__init__("scanner")

        # create map
        map = Map()

        # load file map.json as string
        with open("map.json") as f:
            map.loadMap(f.read())

        
def main():
    rclpy.init()
    pn = ScannerNode()
    rclpy.spin(pn)
    rclpy.shutdown()

if __name__ == "__main__":
    main()